import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import { FiPlus, FiArrowRight } from 'react-icons/fi'
import mapMarkerImg from '../assets/map-marker.svg'
import { Map, TileLayer, Marker, Popup } from 'react-leaflet';
import '../styles/pages/orphanages-map.css'
import MapIcon from '../utils/mapIcon';
import api from '../services/api';

interface Orphanage {
    id: number;
    latitude: number;
    longitude: number;
    name: string;
}

function OrphanagesMap() {
    const [orphanages, setOrphanages] = useState<Orphanage[]>([]);

    useEffect(() => {
        api.get('orphanages').then(response => {
            const orphanages = response.data;
            setOrphanages(orphanages);
        })
    }, [orphanages])

    return (
        <div id="page-map">
            <aside>
                <header>
                    <img src={mapMarkerImg} alt="Happy logo" />

                    <h2>Escolha um orfanato no mapa</h2>  
                    <p>Muitas crianças estão esperando a sua visita :)</p>                  
                </header>

                <footer>
                    <strong>Floranópolis</strong>
                    <span>Santa Catarina</span>
                </footer>
            </aside>

            <Map
                center={[-27.6071361,-48.5746483]}
                zoom={15}
                style={{width: '100%', height: '100%'}}
            >
                {/* <TileLayer url="https://a.tile.openstreetmap.org/{z}/{x}/{y}.png"/> */}
                <TileLayer url={`https://api.mapbox.com/styles/v1/mapbox/light-v10/tiles/256/{z}/{x}/{y}@2x?access_token=${process.env.REACT_APP_MAPBOX_TOKEN}` }/>


                {orphanages.map(orphanage => {
                    return (
                        <Marker
                        key={orphanage.id}
                        icon={MapIcon}
                        position={[orphanage.latitude, orphanage.longitude]}
                        >
                            <Popup className="map-popup" closeButton={false} minWidth={240} maxWidth={240}>
                                {orphanage.name}
                                <Link to={`/orphanages/${orphanage.id}`}>
                                    <FiArrowRight size={20} color="#fff" />
                                </Link>
                            </Popup>
                        </Marker>
                    )
                })}
            </Map>



            <Link to="/orphanages/create" className="create-orphanage">
                <FiPlus size={32} color="#FFF" />
            </Link>
        </div>
    )
}

export default OrphanagesMap;
